import { useEffect } from 'react';
import React from 'react'
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux'
import { useHistory } from 'react-router-dom';
import { auth, provider } from "../firebase";
import { selectUserName, selectUserEmail, selectUserPhoto, setUserLoginDetails, setSignOutState } from '../features/user/userSlice';

const Header = (props) => {
    const dispatch = useDispatch();
    const history = useHistory();
    const userName = useSelector(selectUserName);
    const email = useSelector(selectUserEmail);
    const userPhoto = useSelector(selectUserPhoto);

    useEffect(() => {
        auth.onAuthStateChanged(async (user) => {
            if(user){
                setUser(user);
                history.push("/home");
            }
        });
    }, [userName]);

    const handleAuth = () => {
        if(!userName){
        auth.signInWithPopup(provider).then((result) => {
            setUser(result.user);
        })
        .catch((error) => {
            alert(error.message);
        });
    }else if(userName){
        auth.signOut().then(() => {
            dispatch(setSignOutState());
            history.push("/");
        })

    }
    };

    const setUser = (user) => {
        dispatch(
            setUserLoginDetails({
                name: user.displayName,
                email: user.email,
                photo: user.photoURL,
            }),
        );
    };

    return (
        <Nav>
            <Logo>
                <img src="https://raw.githubusercontent.com/sharikahmad11/hotstar-clone/1806ff6357a04f9476f067e99a8311fc15998338/images/logo.svg" alt="Disney+" />
            </Logo>

            { !userName ? <Login onClick={handleAuth}>Login</Login> :

            <>
            <NavMenu>
                <a href="/home" >
                    <img src="https://raw.githubusercontent.com/sharikahmad11/hotstar-clone/019d12820ebcbc20a6cbfbb96011b4671901d7e4/images/home-icon.svg" alt="HOME" />
                    <span>HOME</span>
                </a>
                <a href="/home" >
                    <img src="https://raw.githubusercontent.com/sharikahmad11/hotstar-clone/019d12820ebcbc20a6cbfbb96011b4671901d7e4/images/search-icon.svg" alt="SEARCH" />
                    <span>SEARCH</span>
                </a>
                <a href="/home" >
                    <img src="https://raw.githubusercontent.com/sharikahmad11/hotstar-clone/019d12820ebcbc20a6cbfbb96011b4671901d7e4/images/watchlist-icon.svg" alt="WATCHLIST" />
                    <span>WATCHLIST</span>
                </a>
                <a href="/home" >
                    <img src="https://raw.githubusercontent.com/sharikahmad11/hotstar-clone/019d12820ebcbc20a6cbfbb96011b4671901d7e4/images/original-icon.svg" alt="ORIGINALS" />
                    <span>ORIGINALS</span>
                </a>
                <a href="/home" >
                    <img src="https://raw.githubusercontent.com/sharikahmad11/hotstar-clone/019d12820ebcbc20a6cbfbb96011b4671901d7e4/images/movie-icon.svg" alt="MOVIES" />
                    <span>MOVIES</span>
                </a>
                <a href="/home" >
                    <img src="https://raw.githubusercontent.com/sharikahmad11/hotstar-clone/019d12820ebcbc20a6cbfbb96011b4671901d7e4/images/series-icon.svg" alt="SERIES" />
                    <span>SERIES</span>
                </a>
            </NavMenu>
            <SignOut>
                <UserImg src={userPhoto} alt={userName}></UserImg>
                <DropDown>
                    <span onClick={handleAuth}>Sign out</span>
                </DropDown>
            </SignOut>
            
            </>
}
        </Nav>
    )
}

const Nav = styled.nav`
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    height: 70px;
    background-color: #090b13;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 36px;
    letter-spacing: 16px;
    z-index: 3;
`;

const Logo = styled.a`
    padding: 0;
    width: 80px;
    margin-top: 4px;
`;

const NavMenu = styled.div`
    align-items: center;
    display: flex;
    flex-flow: row nowrap;
    height: 100%;
    justify-content: flex-end;
    margin: 0px;
    padding: 0px;
    position: relative;
    margin-right: auto;
    margin-left: 25px;

    a {
        display: flex;
        align-items: center;
        padding: 0 12px;

        img {
            height: 22px;
            min-width: 20px;
            width: 22px;
            z-index: auto;
            padding: 0 0 2.5px;
        }

        span {
            color: rgb(249, 249, 249);
            font-size: 13px;
            letter-spacing: 1.42px;
            line-height: 1.08;
            padding: 2px 0px;
            white-space: nowrap;
            position: relative;       
        

            &: before {
                background-color: rgb(249, 249, 249);
                border-radius: 0px 0px 4px 4px;
                bottom: -6px;
                content: "";
                height: 2px;
                left: 0px;
                opacity: 0;
                position: absolute;
                right: 0px;
                transform-origin: left center;
                transform: scaleX(0);
                transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
                visibility: hidden;
                width: auto;
            }
        }

        &: hover {
            span: before {
                transform: scaleX(1);
                visibility: visible;
                opacity: 1 !important;

            }
        }
    

    @media (max-width: 768px){
        display: none;
    }
`;

const Login = styled.a`
    background-color: rgb(0, 0, 0, 0.6);
    padding: 8px 16px;
    text-transform: uppercase;
    letter-spacing: 1.5px;
    border: 1px solid #f9f9f9;
    border-radius: 4px;
    transition: all 0.2s ease 0s;

    &: hover {
        background-color: #f9f9f9;
        color: #000;
        border-color: transparent;
    }
`;

const UserImg = styled.img`
    height: 100%;
`;

const DropDown = styled.div`
    position: absolute;
    top : 48px;
    right: 0px;
    background: rgb(19, 19, 19);
    border: 1px solid rgb(151, 151, 151, 0.34);
    border-radius: 4px;
    box-shadow: rgb(0 0 0 / 50%) 0px 0px 18px 0px;
    padding: 10px;
    font-size: 14px;
    letter-spacing: 3px;
    width: 100px;
    opacity: 0;
`;

const SignOut = styled.div`
    position: relative;
    height: 48px;
    width: 48px;
    display: flex;
    cursor: pointer;
    align-items: center;
    justify-content: center;

    ${UserImg} {
        border-radius: 50%;
        width: 100%;
        height: 100%;
    }

    &:hover {
        ${DropDown} {
            opacity: 1;
            transition-duration: 1s;
        }
    }
`;
export default Header
